// Dependencies
import axios from "axios";
import { properties } from "../../config/properties";

class EmployeeApiGenerated {

  static contextUrl = properties.endpoint + "/employee";

  // CRUD METHODS

  /**
  * EmployeeService.create
  *   @description CRUD ACTION create
  *
  */
  static createEmployee(employee) {
    return axios.post(EmployeeApiGenerated.contextUrl, employee )
      .then(response => {
        return response.data;
      })
      .catch(error => {
        throw error;
      });
  }

  /**
  * EmployeeService.delete
  *   @description CRUD ACTION delete
  *   @param ObjectId id Id
  *
  */
  static deleteEmployee(id) {
    return axios.delete(EmployeeApiGenerated.contextUrl + "/" + id)
      .then(response => {
        return response.data;
      })
      .catch(error => {
        throw error;
      });
  }

  /**
  * EmployeeService.get
  *   @description CRUD ACTION get
  *   @param ObjectId id Id resource
  *
  */
  static getOneEmployee(id) {
    return axios.get(EmployeeApiGenerated.contextUrl + "/" + id)
      .then(response => {
        return response.data;
      })
      .catch(error => {
        throw error;
      });
  }

  /**
  * EmployeeService.list
  *   @description CRUD ACTION list
  *
  */
  static getEmployeeList() {
    return axios.get(EmployeeApiGenerated.contextUrl)
      .then(response => {
        return response.data;
      })
      .catch(error => {
        throw error;
      });
  }

  /**
  * EmployeeService.update
  *   @description CRUD ACTION update
  *   @param ObjectId id Id
  *
  */
  static saveEmployee(employee) {
    return axios.post(EmployeeApiGenerated.contextUrl + "/" + employee._id, employee )
      .then(response => {
        return response.data;
      })
      .catch(error => {
        throw error;
      });
  }



    // Custom APIs
  /**
  * EmployeeService.me
  *   @description get my profile
  *   @returns Boolean
  *
  */
  static me(...params) {
    return axios.get(EmployeeApiGenerated.contextUrl + '/me' )
      .then(response => {
        return response.data;
      })
      .catch(error => {
        throw error;
      });
  }

}

export default EmployeeApiGenerated;
